import React, {useState} from 'react';
import {send} from 'emailjs-com';

function Contact() {
  const [toSend, setToSend] = useState({
    to_name: '',
    from_name: '',
    email: '',
    message: ''
  })

  return (
    <div>
      <main className="contactContainer">
        <form id="contact" onSubmit={onSubmit}>
          <input alt="Name" placeholder='Name' value={toSend.from_name} onChange={handleChange}/>
          <input alt="email address" placeholder='email@address.com' value={toSend.email} onChange={handleChange}/>
          <textarea className="messageInput" rows="2" col="1" alt="Message" placeholder='Your Message' value={toSend.message} onChange={handleChange}/>
        </form>
        <a type="button" className="button" form="#contact">Submit</a>
      </main>
    </div>
  );

  const onSubmit = (e) => {
    e.preventDefault();
    send(
      'service_kah3hlj',
      'template_7wnegpk',
      toSend,
      'USER ID'
    )
    .then((response) => {
      console.log('SUCCESS!', response.status,response.text);
    })
    .catch((err) => {

    })
  };

  const handleChange = (e) => {
    setToSend({ ...toSend, [e.target.name]: e.target.value});
  }

}

export default Contact;
