  //     gameCard.setAttribute('data-visible', true);
  //     card.scrollIntoView({behavior:"smooth"});
  //   }
  //   else {
  //     card.setAttribute('data-visible', false);
  //     cardToggle.setAttribute("aria-expanded", false);
  //     gameCard.setAttribute('data-visible', false);
  //   }
  //
  // }
}

export default Game;
